package Operators;

public class arithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//operator brings two things together
		int a = 10;
		int b = 20;
		
		//arithmetic operators
		//sum of two numbers
		//when bringing two things together a +b must be in brackets
		System.out.println("Sum of A and B is: " + (a + b));
		// will not work - System.out.println("Sum of A and B is: " a + b);

	}

}
