package ifElse;

public class ifElse1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Control statement -- Topic
		//--> Conditional Statement (Selection Statements)
		// - /* -- at the b
		// - */
		//--> if..else
		
		//if (condition)
		//{
			//statement
		//}
		//else
		//{
			//statement
		
		//Verify voting age code
		
		int  age = 20;
		//
		if( age >= 18 ) //Condition statement -- don't close if
			
		{ //open bracket before statement
			
			System.out.println("Eligible for vote"); //make the statement
			
		} //close bracket after statement
		
		else //write else -- don't close else
			
		{ //open bracket before statement
			
			System.out.println("Not eligible for vote");
			
		} //close bracket after statement
		
	}

}
